<?phpclass Login_model extends CI_Model{    function __construct(){        parent::__construct();    }        public function validate() {		$username = $this->security->xss_clean($this->input->post('username'));        $password = $this->security->xss_clean($this->input->post('password'));			$this->db->select('User-ID AS UserID, Location, Age, Username, Password, E-mail, Avatar, DateTime, IsAdmin');        $this->db->where('Username', $username);        $this->db->where('Password', sha1($password));				$query = $this->db->get('bx-users');		        if($query->num_rows == 1)        {            $row = $query->row();            $data = array(                    'userid' => $row->UserID,                    'username' => $row->Username,                    'validated' => true,					'is_admin' => $row->IsAdmin                    );            $this->session->set_userdata($data);            return true;        }        return false;    }		public function unique_username($username)	{		$this->db->from('bx-users');		$this->db->where('username', $username);		$query = $this->db->get();				if($query->num_rows() != 0) return FALSE;		return TRUE;	}		public function register() {		$username = $this->security->xss_clean($this->input->post('username'));        $password = $this->security->xss_clean($this->input->post('password'));		$email = $this->security->xss_clean($this->input->post('email'));				$data = array(		   'Username' => $username,		   'Password' => sha1($password),		   'E-mail' => $email,		   'DateTime' => date("Y-m-d h-m-s")		);				$this->db->insert('bx-users', $data);	}}